import { createAsyncThunk } from '@reduxjs/toolkit';
import { setConfigs, setLimits } from '../reducers/limitsAndConfigsSlice';

export const getLimits = createAsyncThunk(
  'limits/getLimits',
  async (_, { dispatch }) => {
    try {
      const limits = {
        maxBet: 100,
        minBet: 1,
        maxWin: 5000,
        currency: 'TZS',
      };
      const configs = {
        jackpotAmount: 500000,
        lastWinName: 'Arusha Marush',
        lastWinAmount: 540000,
        currency: 'TZS',
      };
      dispatch(setLimits(limits));
      dispatch(setConfigs(configs));
      return limits;
    } catch (error) {
      dispatch(getLimits.rejected(error.message));
      throw new Error('Error fetching limits data');
    }
  },
);
