import { createAsyncThunk } from '@reduxjs/toolkit';
import { setConfigs, setLimits } from '../reducers/limitsAndConfigsSlice';
import axios from 'axios';

export const getLimitsAndConfigs = createAsyncThunk(
  'limits/getLimits',
  async (_, { dispatch }) => {
    try {
      const limitsResponse = await axios.get(
        'http://oliver.energaming.systems:20034/api/v1/get-game-versions',
        {
          params: {
            token: 'test-frontend-token',
            duration: 3,
            page: 0,
          },
        },
      );

      const limits = {
        maxBet: response.data.max_bet,
        minBet: response.data.min_bet,
        maxWin: response.data.max_win,
        currency: response.data.currency,
      };

      const configs = {
        jackpotAmount: response.data.jackpot_amount,
        lastWinName: response.data.last_win_name,
        lastWinAmount: response.data.last_win_amount,
        currency: response.data.currency,
      };

      dispatch(setLimits(limitsResponse));
      dispatch(setConfigs(configs));

      return limits;
    } catch (error) {
      console.error('Error fetching limits data:', error);
      throw new Error('Error fetching limits data');
    }
  },
);
