import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useTimer } from 'react-timer-hook';
import { getLimits } from '../../Redux/thunks/limitsThunks';
import { getFrequency } from '../../Redux/thunks/frequencyThunks';
import { getMarkets } from '../../Redux/thunks/marketsThunks';
import { getHistory } from '../../Redux/thunks/historyThunks';
import { getTimer, selectTimer } from '../../Redux/reducers/timerSlice';
import {} from '../../Redux/reducers/timerSlice';

const Timer = () => {
  const dispatch = useDispatch();
  const expiryTimestamp = useSelector(selectTimer);

  if (!expiryTimestamp) {
    return null;
  }

  const time = new Date();
  const timeOnSeconds = time.setSeconds(time.getSeconds() + expiryTimestamp);

  const { seconds, minutes } = useTimer({
    expiryTimestamp: timeOnSeconds,
    onExpire: () => {
      console.log('asa');
    },
  });

  useEffect(() => {
    console.log(seconds + 'aper');

    if (seconds === 0 && minutes === 0) {
      console.log('Timer has expired! Fetching new data...');
      dispatch(getLimits());
      dispatch(getFrequency());
      dispatch(getMarkets());
      dispatch(getHistory());
      dispatch(getTimer());
    }
  }, [seconds, minutes, dispatch]);

  return (
    <span>
      <span>{minutes}</span>:<span>{seconds}</span>
    </span>
  );
};

export default Timer;
