import { createSlice } from '@reduxjs/toolkit';
import { getLimitsData } from '../thunks/limitsThunks';

const initialState = {
  maxBet: null,
  minBet: null,
  maxWin: null,
  currency: null,
  status: 'idle',
  error: null,
};

export const limitsSlice = createSlice({
  name: 'limits',
  initialState,
  reducers: {
    setLimits: (state, action) => {
      state.maxBet = action.payload.maximalBet;
      state.minBet = action.payload.minimalBet;
      state.maxWin = action.payload.maxWin;
      state.currency = action.payload.currency;
    },
  },
  extraReducers: builder => {
    builder
      .addCase(getLimitsData.pending, state => {
        state.status = 'loading';
      })
      .addCase(getLimitsData.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.maxBet = action.payload.maximalBet;
        state.minBet = action.payload.minimalBet;
        state.maxWin = action.payload.maxWin;
        state.currency = action.payload.currency;
        state.error = null;
      })
      .addCase(getLimitsData.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      });
  },
});

export const setLimits = state => state.limits;

export default limitsSlice.reducer;
