import { createSlice } from '@reduxjs/toolkit';
import { getLimits } from '../thunks/limitsThunks';

const initialState = {
  maxBet: null,
  minBet: null,
  maxWin: null,
  currency: null,
  error: null,
};

export const limitsSlice = createSlice({
  name: 'limits',
  initialState,
  reducers: {
    setLimits: (state, action) => {
      state.maxBet = action.payload.maxBet;
      state.minBet = action.payload.minBet;
      state.maxWin = action.payload.maxWin;
      state.currency = action.payload.currency;
    },
  },
  extraReducers: builder => {
    builder
      .addCase(getLimits.pending, state => {
        state.status = 'loading';
        state.error = null;
      })
      .addCase(getLimits.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.error = null;
        state.maxBet = action.payload.maxBet;
        state.minBet = action.payload.minBet;
        state.maxWin = action.payload.maxWin;
        state.currency = action.payload.currency;
      })
      .addCase(getLimits.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.payload;
      });
  },
});

export const { setLimits } = limitsSlice.actions; // Update here to define setLimits as an action creator

export default limitsSlice.reducer;
