import { createAsyncThunk } from '@reduxjs/toolkit';
import { setLimits } from '../reducers/limitsSlice';

const fetchLimits = async () => {
  // Simulate an API request delay with setTimeout
  return new Promise(resolve =>
    setTimeout(resolve, 2000, {
      maxBet: 100,
      minBet: 1,
      maxWin: 5000,
      currency: 'USD',
    }),
  );
};

export const getLimits = createAsyncThunk(
  'limits/getLimits',
  async (_, { dispatch }) => {
    try {
      dispatch(getLimits.pending()); // Dispatch pending action
      const limits = await fetchLimits();
      dispatch(setLimits(limits));
      return limits;
    } catch (error) {
      dispatch(getLimits.rejected(error.message));
      throw new Error('Error fetching limits data');
    }
  },
);
