import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  maxBet: null,
  minBet: null,
  maxWin: null,
  currency: null,
  error: null,
};

export const limitsAndConfigsSlice = createSlice({
  name: 'limits',
  initialState,
  reducers: {
    setLimits: (state, action) => {
      // state.maxBet = action.payload.maximalBet;
      // state.minBet = action.payload.minimalBet;
      // state.maxWin = action.payload.maxWin;
      // state.currency = action.payload.currency;
      state.maxBet = action.payload.mini;
      state.minBet = action.payload.minBet;
      state.maxWin = action.payload.maxWin;
      state.currency = action.payload.currency;
    },
    setConfigs: (state, action) => {
      state.jackpotAmount = action.payload.jackpotAmount;
      state.lastWinName = action.payload.lastWinName;
      state.lastWinAmount = action.payload.lastWinAmount;
      state.currency = action.payload.currency;
    },
  },
});

export const { setLimits, setConfigs } = limitsAndConfigsSlice.actions;

export default limitsAndConfigsSlice.reducer;
