import { createSlice } from '@reduxjs/toolkit';
import { getLimits } from '../thunks/limitsThunks';

const initialState = {
  maxBet: null,
  minBet: null,
  maxWin: null,
  currency: null,
  error: null,
};

export const limitsSlice = createSlice({
  name: 'limits',
  initialState,
  reducers: {
    setLimits: (state, action) => {
      state.maxBet = action.payload.maxBet;
      state.minBet = action.payload.minBet;
      state.maxWin = action.payload.maxWin;
      state.currency = action.payload.currency;
    },
    setConfigs: (state, action) => {
      state.jackpotAmount = action.payload.jackpotAmount;
      state.lastWinName = action.payload.lastWinName;
      state.lastWinAmount = action.payload.lastWinAmount;
      state.currency = action.payload.currency;
    },
  },
});

export const { setLimits, setConfigs } = limitsSlice.actions;

export default limitsSlice.reducer;
