import { createAsyncThunk } from '@reduxjs/toolkit';
import { setMarkets } from '../reducers/marketSlice';
import axios from 'axios';

export const markets = [
  {
    market_id: 1,
    market_title: 'Market 1',
    prices: [
      { price_id: 1, price_name: 'Price 1', rate: 10 },
      { price_id: 2, price_name: 'Price 2', rate: 20 },
    ],
  },
  {
    market_id: 2,
    market_title: 'Market 2',
    prices: [
      { price_id: 4, price_name: 'Price 4', rate: 40 },
      { price_id: 5, price_name: 'Price 5', rate: 50 },
    ],
  },
];

export const getMarkets = createAsyncThunk(
  'markets/getMarkets',
  async (_, { dispatch }) => {
    try {
      const response = await axios.get(
        `http://oliver.energaming.systems:20034/api/v1/markets/get`,
        {
          params: {
            token: 'test-frontend-token',
            duration: 3,
            page: 0,
          },
        },
      );

      const markets = response.data.map(market => {
        return {
          market_id: market.id,
          market_title: market.title,
          prices: market.prices.map(price => {
            return {
              price_id: price.id,
              price_name: price.name,
              rate: price.rate,
            };
          }),
        };
      });
      dispatch(setMarkets(markets));
      return markets;
    } catch (error) {
      throw new Error('Error fetching markets data');
    }
  },
);
