import React, { useEffect, useState } from 'react';
import s from './BetBoard.module.scss';
import BetNum from '../BetNum/BetNum';
import { useSelector } from 'react-redux';

const BetBoard = () => {
  const { coldNums, hotNums } = useSelector(state => state.frequency);
  const { drawNums } = useSelector(state => state.history);

  const [activeNums, setActiveNums] = useState([]);

  useEffect(() => {
    if (drawNums.length > 0) {
      setActiveNums(drawNums);
      const timer = setTimeout(() => {
        setActiveNums([]);
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [drawNums]);

  return (
    <div className={s.betBoard}>
      <ul className={s.betBoard__list}>
        {Array.from(Array(80), (_, i) => {
          const number = i + 1;
          const active = activeNums.includes(number.toString());
          const cold = coldNums.includes(number.toString());
          const hot = hotNums.includes(number.toString());
          return (
            <BetNum active={active} cold={cold} hot={hot} key={i}>
              {number}
            </BetNum>
          );
        })}
      </ul>
    </div>
  );
};

export default BetBoard;
