import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  drawNums: [],
  history: [],
  currentDrawId: '',
  nextDrawId: '',
};

export const historySlice = createSlice({
  name: 'history',
  initialState,
  reducers: {
    setHistory: (state, action) => {
      state.drawNums = action.payload.data.data.lastRaces[0].r.split(',');
      state.history = action.payload.data.data.lastRaces.slice(1, 4);
      state.currentDrawId = action.payload.data.data.lastRaces[0].id;
      state.nextDrawId = action.payload.data.data.drawInfo.drawId;
    },
  },
});

export const { setHistory } = historySlice.actions;

export default historySlice.reducer;
