import React, { useState, useEffect } from 'react';
import s from './BetBoard.module.scss';
import BetNum from '../BetNum/BetNum';
import { useSelector } from 'react-redux';

const BetBoard = () => {
  const { coldNums, hotNums } = useSelector(state => state.frequency);
  const { drawNums } = useSelector(state => state.history);
  const [currentIndex, setCurrentIndex] = useState([drawNums[0]]);

  useEffect(() => {
    let i = 0;
    const intervalId = setInterval(() => {
      if (drawNums.length > i) {
        const nextNum = drawNums[i];
        setCurrentIndex([nextNum]);
        i++;
        console.log(i + '===' + drawNums.length - 1);
      } else if (i === drawNums.length - 1) {
        setCurrentIndex([]);
      }
    }, 3500);

    return () => clearInterval(intervalId);
  }, [drawNums]);

  return (
    <div className={s.betBoard}>
      <ul className={s.betBoard__list}>
        {Array.from(Array(80), (_, i) => {
          const number = i + 1;
          return (
            <BetNum
              active={currentIndex.includes(String(number))}
              cold={coldNums.includes(String(number))}
              hot={hotNums.includes(String(number))}
              key={i}
              highlight={drawNums[currentIndex] === number}>
              {number}
            </BetNum>
          );
        })}
      </ul>
    </div>
  );
};

export default BetBoard;
